// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@redstone-finance/evm-connector/contracts/data-services/MainDemoConsumerBase.sol";

/**
 * @title MEVDetector
 * @dev Core MEV detection algorithms for Aegilon Protocol
 * @notice Detects sandwich attacks, front-running, and arbitrage-based MEV
 */
contract MEVDetector is Ownable, MainDemoConsumerBase {

    // Risk threshold in basis points (500 = 5%)
    uint256 public riskThreshold = 500;

    // Minimum price delta to consider significant
    uint256 public minPriceDelta = 100; // 1% in basis points

    // Front-running gas threshold multiplier (120 = 20% above average)
    uint256 public frontRunGasMultiplier = 120;

    // Track last known prices for each feed
    mapping(bytes32 => uint256) public lastPrices;
    mapping(bytes32 => uint256) public lastUpdateTimestamp;

    // Track historical gas prices for baseline
    uint256[] private gasHistory;
    uint256 private gasHistoryIndex;
    uint256 public constant GAS_HISTORY_SIZE = 100;

    // MEV threat types
    enum ThreatType {
        SANDWICH,
        FRONTRUN,
        ARBITRAGE,
        UNKNOWN
    }

    // Events
    event MEVThreatDetected(
        address indexed user,
        bytes32 indexed feedId,
        ThreatType threatType,
        uint256 riskScore,
        uint256 timestamp
    );

    event PriceAnomalyDetected(
        bytes32 indexed feedId,
        uint256 expectedPrice,
        uint256 actualPrice,
        uint256 delta
    );

    event ConfigurationUpdated(
        string parameter,
        uint256 oldValue,
        uint256 newValue
    );

    constructor() Ownable() {
        // Initialize gas history array
        for (uint i = 0; i < GAS_HISTORY_SIZE; i++) {
            gasHistory.push(0);
        }
        // Transfer ownership to deployer
        _transferOwnership(msg.sender);
    }

    /**
     * @dev Main MEV detection function for price-based attacks
     * @param feedId The price feed identifier (e.g., "ETH", "USDC")
     * @param expectedPrice Expected price from user's perspective
     * @param txGasPrice Gas price of the current transaction
     * @return isThreat Whether MEV threat was detected
     * @return threatType Type of MEV threat detected
     * @return riskScore Risk score (0-10000 basis points)
     */
    function detectMEV(
        bytes32 feedId,
        uint256 expectedPrice,
        uint256 txGasPrice
    ) external onlyOwner returns (
        bool isThreat,
        ThreatType threatType,
        uint256 riskScore
    ) {
        // Get current oracle price
        uint256 currentPrice = getOracleNumericValueFromTxMsg(feedId);

        // Update price history
        lastPrices[feedId] = currentPrice;
        lastUpdateTimestamp[feedId] = block.timestamp;

        // Calculate price delta and slippage
        uint256 delta = currentPrice > expectedPrice ? 
            currentPrice - expectedPrice : 
            expectedPrice - currentPrice;
        uint256 slippagePercentage = (delta * 10000) / expectedPrice;

        // Update gas history
        _updateGasHistory(txGasPrice);

        // Detect sandwich attack (high slippage)
        if (slippagePercentage > riskThreshold) {
            riskScore = slippagePercentage;
            threatType = ThreatType.SANDWICH;
            isThreat = true;

            emit MEVThreatDetected(
                msg.sender,
                feedId,
                threatType,
                riskScore,
                block.timestamp
            );

            emit PriceAnomalyDetected(
                feedId,
                expectedPrice,
                currentPrice,
                delta
            );

            return (isThreat, threatType, riskScore);
        }

        // Detect front-running (abnormal gas price)
        uint256 avgGasPrice = _getAverageGasPrice();
        if (avgGasPrice > 0 && _detectFrontRunning(txGasPrice, avgGasPrice)) {
            riskScore = ((txGasPrice - avgGasPrice) * 10000) / avgGasPrice;
            threatType = ThreatType.FRONTRUN;
            isThreat = true;

            emit MEVThreatDetected(
                msg.sender,
                feedId,
                threatType,
                riskScore,
                block.timestamp
            );

            return (isThreat, threatType, riskScore);
        }

        // Detect arbitrage patterns (rapid price changes)
        if (_detectArbitragePattern(feedId, currentPrice)) {
            riskScore = 300; // Fixed score for arbitrage detection
            threatType = ThreatType.ARBITRAGE;
            isThreat = true;

            emit MEVThreatDetected(
                msg.sender,
                feedId,
                threatType,
                riskScore,
                block.timestamp
            );

            return (isThreat, threatType, riskScore);
        }

        return (false, ThreatType.UNKNOWN, 0);
    }

    /**
     * @dev Simplified front-running detection
     * @param txGasPrice Current transaction gas price
     * @param avgGasPrice Average historical gas price
     * @return Whether front-running is detected
     */
    function _detectFrontRunning(
        uint256 txGasPrice,
        uint256 avgGasPrice
    ) internal view returns (bool) {
        return txGasPrice > (avgGasPrice * frontRunGasMultiplier / 100);
    }

    /**
     * @dev Detect arbitrage patterns based on rapid price movements
     * @param feedId Price feed identifier
     * @param currentPrice Current price from oracle
     * @return Whether arbitrage pattern is detected
     */
    function _detectArbitragePattern(
        bytes32 feedId,
        uint256 currentPrice
    ) internal view returns (bool) {
        uint256 lastPrice = lastPrices[feedId];
        uint256 lastUpdate = lastUpdateTimestamp[feedId];

        // Skip if no historical data
        if (lastPrice == 0 || lastUpdate == 0) {
            return false;
        }

        // Check for rapid price change (within 30 seconds)
        if (block.timestamp - lastUpdate <= 30) {
            uint256 priceChange = currentPrice > lastPrice ?
                currentPrice - lastPrice :
                lastPrice - currentPrice;
            uint256 changePercentage = (priceChange * 10000) / lastPrice;

            // Flag if price changed more than minPriceDelta in short time
            return changePercentage > minPriceDelta;
        }

        return false;
    }

    /**
     * @dev Update gas price history for baseline calculation
     * @param gasPrice Current gas price to add to history
     */
    function _updateGasHistory(uint256 gasPrice) internal {
        gasHistory[gasHistoryIndex] = gasPrice;
        gasHistoryIndex = (gasHistoryIndex + 1) % GAS_HISTORY_SIZE;
    }

    /**
     * @dev Calculate average gas price from history
     * @return Average gas price
     */
    function _getAverageGasPrice() internal view returns (uint256) {
        uint256 sum = 0;
        uint256 count = 0;

        for (uint i = 0; i < GAS_HISTORY_SIZE; i++) {
            if (gasHistory[i] > 0) {
                sum += gasHistory[i];
                count++;
            }
        }

        return count > 0 ? sum / count : 0;
    }

    /**
     * @dev Get current price for a feed (external view function)
     * @param feedId Price feed identifier
     * @return Current price from oracle
     */
    function getCurrentPrice(bytes32 feedId) external view returns (uint256) {
        return getOracleNumericValueFromTxMsg(feedId);
    }

    /**
     * @dev Get historical price data
     * @param feedId Price feed identifier
     * @return lastPrice Last recorded price
     * @return lastUpdate Timestamp of last update
     */
    function getPriceHistory(bytes32 feedId) external view returns (
        uint256 lastPrice,
        uint256 lastUpdate
    ) {
        return (lastPrices[feedId], lastUpdateTimestamp[feedId]);
    }

    // Configuration functions
    function setRiskThreshold(uint256 newThreshold) external onlyOwner {
        uint256 oldThreshold = riskThreshold;
        riskThreshold = newThreshold;
        emit ConfigurationUpdated("riskThreshold", oldThreshold, newThreshold);
    }

    function setMinPriceDelta(uint256 newDelta) external onlyOwner {
        uint256 oldDelta = minPriceDelta;
        minPriceDelta = newDelta;
        emit ConfigurationUpdated("minPriceDelta", oldDelta, newDelta);
    }

    function setFrontRunGasMultiplier(uint256 newMultiplier) external onlyOwner {
        uint256 oldMultiplier = frontRunGasMultiplier;
        frontRunGasMultiplier = newMultiplier;
        emit ConfigurationUpdated("frontRunGasMultiplier", oldMultiplier, newMultiplier);
    }

    /**
     * @dev Emergency function to get average gas price (external view)
     * @return Current average gas price from history
     */
    function getAverageGasPrice() external view returns (uint256) {
        return _getAverageGasPrice();
    }
}

contracts/MEVProtector.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./AegilonToken.sol";

/**
 * @title MEVProtector
 * @dev Core contract for detecting and preventing MEV attacks on Etherlink
 * 
 * Features:
 * - Real-time MEV threat detection
 * - Automated protection mechanisms
 * - Integration with Redstone oracles for price validation
 * - Goldsky integration for transaction monitoring
 * - $AEG token rewards for threat reporting
 */
contract MEVProtector is Ownable, ReentrancyGuard, Pausable {

    // Core state variables
    AegilonToken public aegilonToken;

    // MEV detection parameters
    uint256 public priceDeviationThreshold = 200; // 2% in basis points
    uint256 public volumeAnomalyThreshold = 500; // 5% volume spike threshold
    uint256 public timeWindowForDetection = 5; // 5 seconds window for MEV detection
    uint256 public minimumLiquidityForProtection = 1000 * 10**18; // Minimum $1000 equivalent

    // Protection status tracking
    mapping(address => bool) public isProtected;
    mapping(address => uint256) public protectionLevel;
    mapping(address => uint256) public lastTransactionTime;
    mapping(address => uint256) public transactionCount;

    // MEV threat tracking
    mapping(bytes32 => bool) public detectedThreats;
    mapping(address => uint256) public threatReports;
    mapping(address => uint256) public successfulPrevented;

    // Oracle integration (Redstone)
    mapping(string => uint256) public lastOraclePrices;
    mapping(string => uint256) public lastOracleUpdate;

    // Goldsky integration endpoints
    string public goldkySubgraphURL;
    address public authorizedIndexer;

    // Reward parameters
    uint256 public threatReportReward = 10 * 10**18; // 10 AEG base reward
    uint256 public protectionSuccessReward = 5 * 10**18; // 5 AEG for successful protection

    // Events
    event ProtectionActivated(address indexed user, uint256 level);
    event ProtectionDeactivated(address indexed user);
    event MEVThreatDetected(
        bytes32 indexed threatId,
        address indexed target,
        address indexed attacker,
        uint256 threatType,
        uint256 timestamp
    );
    event MEVThreatPrevented(
        bytes32 indexed threatId,
        address indexed protectedUser,
        uint256 savedAmount,
        uint256 timestamp
    );
    event ThreatReported(address indexed reporter, bytes32 indexed threatId, uint256 reward);
    event OraclePriceUpdated(string indexed symbol, uint256 price, uint256 timestamp);

    // MEV threat types
    enum ThreatType {
        SandwichAttack,
        Frontrunning,
        Backrunning,
        JITLiquidity,
        Arbitrage,
        LiquidationMEV
    }

    // Threat detection structure
    struct ThreatData {
        address target;
        address attacker;
        ThreatType threatType;
        uint256 detectedAt;
        uint256 estimatedLoss;
        bool prevented;
        address reporter;
    }

    mapping(bytes32 => ThreatData) public threats;

    // Modifiers
    modifier onlyProtected() {
        require(isProtected[msg.sender], "User not protected");
        _;
    }

    modifier onlyIndexer() {
        require(msg.sender == authorizedIndexer, "Only authorized indexer");
        _;
    }

    constructor(address _aegilonToken) {
        require(_aegilonToken != address(0), "Invalid token address");
        aegilonToken = AegilonToken(_aegilonToken);
    }

    /**
     * @dev Activate MEV protection for the caller
     */
    function activateProtection() external nonReentrant whenNotPaused {
        uint256 userProtectionLevel = aegilonToken.getProtectionLevel(msg.sender);
        require(userProtectionLevel > 0, "Insufficient AEG tokens staked for protection");

        isProtected[msg.sender] = true;
        protectionLevel[msg.sender] = userProtectionLevel;

        emit ProtectionActivated(msg.sender, userProtectionLevel);
    }

    /**
     * @dev Deactivate MEV protection
     */
    function deactivateProtection() external {
        require(isProtected[msg.sender], "Protection not active");

        isProtected[msg.sender] = false;
        protectionLevel[msg.sender] = 0;

        emit ProtectionDeactivated(msg.sender);
    }

    /**
     * @dev Detect MEV threat based on transaction patterns (called by Goldsky indexer)
     * @param target Address being targeted
     * @param attacker Suspected attacker address
     * @param threatType Type of MEV attack
     * @param transactionData Encoded transaction data for analysis
     */
    function detectMEVThreat(
        address target,
        address attacker,
        uint8 threatType,
        bytes calldata transactionData
    ) external onlyIndexer returns (bytes32 threatId) {
        threatId = keccak256(abi.encodePacked(
            target,
            attacker,
            threatType,
            block.timestamp,
            transactionData
        ));

        require(!detectedThreats[threatId], "Threat already detected");

        // Analyze threat severity
        uint256 estimatedLoss = analyzeThreatSeverity(
            target,
            attacker,
            ThreatType(threatType),
            transactionData
        );

        // Store threat data
        threats[threatId] = ThreatData({
            target: target,
            attacker: attacker,
            threatType: ThreatType(threatType),
            detectedAt: block.timestamp,
            estimatedLoss: estimatedLoss,
            prevented: false,
            reporter: msg.sender
        });

        detectedThreats[threatId] = true;

        emit MEVThreatDetected(threatId, target, attacker, threatType, block.timestamp);

        // Attempt automatic protection if user is protected
        if (isProtected[target]) {
            bool prevented = executeProtection(threatId);
            if (prevented) {
                threats[threatId].prevented = true;
                successfulPrevented[target]++;

                // Reward protection success
                aegilonToken.rewardThreatReporter(target, protectionSuccessReward);

                emit MEVThreatPrevented(threatId, target, estimatedLoss, block.timestamp);
            }
        }

        return threatId;
    }

    /**
     * @dev Execute protection mechanism against detected threat
     * @param threatId ID of the detected threat
     * @return success Whether protection was successful
     */
    function executeProtection(bytes32 threatId) internal returns (bool success) {
        ThreatData storage threat = threats[threatId];
        require(threat.detectedAt > 0, "Threat not found");
        require(isProtected[threat.target], "Target not protected");

        // Protection logic based on threat type and user protection level
        uint256 userLevel = protectionLevel[threat.target];

        if (threat.threatType == ThreatType.SandwichAttack) {
            success = preventSandwichAttack(threat, userLevel);
        } else if (threat.threatType == ThreatType.Frontrunning) {
            success = preventFrontrunning(threat, userLevel);
        } else if (threat.threatType == ThreatType.JITLiquidity) {
            success = preventJITLiquidity(threat, userLevel);
        } else {
            // Generic protection for other threat types
            success = executeGenericProtection(threat, userLevel);
        }

        return success;
    }

    /**
     * @dev Prevent sandwich attacks by adjusting gas prices or delaying execution
     */
    function preventSandwichAttack(
        ThreatData memory threat,
        uint256 protectionLevel
    ) internal pure returns (bool) {
        // Implementation would include:
        // - Gas price adjustment to front-run the front-runner
        // - Transaction reordering through Etherlink's fast finality
        // - Slippage protection enhancement

        // For now, return success based on protection level
        return protectionLevel >= 2; // Requires at least level 2 protection
    }

    /**
     * @dev Prevent front-running attacks
     */
    function preventFrontrunning(
        ThreatData memory threat,
        uint256 protectionLevel
    ) internal pure returns (bool) {
        // Implementation would include:
        // - Private mempool submission
        // - Commit-reveal schemes
        // - Time-locked transactions

        return protectionLevel >= 1; // Basic protection sufficient
    }

    /**
     * @dev Prevent Just-In-Time liquidity attacks
     */
    function preventJITLiquidity(
        ThreatData memory threat,
        uint256 protectionLevel
    ) internal pure returns (bool) {
        // Implementation would include:
        // - Liquidity source verification
        // - Alternative routing
        // - Pool selection optimization

        return protectionLevel >= 3; // Requires higher protection level
    }

    /**
     * @dev Generic protection mechanism
     */
    function executeGenericProtection(
        ThreatData memory threat,
        uint256 protectionLevel
    ) internal pure returns (bool) {
        // Basic protection measures for unspecified threats
        return protectionLevel >= 1;
    }

    /**
     * @dev Analyze threat severity and estimate potential loss
     */
    function analyzeThreatSeverity(
        address target,
        address attacker,
        ThreatType threatType,
        bytes calldata transactionData
    ) internal view returns (uint256 estimatedLoss) {
        // Basic analysis - would be enhanced with actual transaction data parsing
        if (threatType == ThreatType.SandwichAttack) {
            estimatedLoss = 1000 * 10**18; // Placeholder: $1000 estimated loss
        } else if (threatType == ThreatType.Frontrunning) {
            estimatedLoss = 500 * 10**18; // Placeholder: $500 estimated loss
        } else {
            estimatedLoss = 100 * 10**18; // Placeholder: $100 estimated loss
        }

        return estimatedLoss;
    }

    /**
     * @dev Report a MEV threat manually (community reporting)
     * @param threatId ID of the threat to report
     */
    function reportThreat(bytes32 threatId) external nonReentrant {
        require(detectedThreats[threatId], "Threat not detected");
        require(threats[threatId].reporter != msg.sender, "Cannot report own detection");

        threatReports[msg.sender]++;

        // Reward the reporter
        aegilonToken.rewardThreatReporter(msg.sender, threatReportReward);

        emit ThreatReported(msg.sender, threatId, threatReportReward);
    }

    /**
     * @dev Update oracle price (called by authorized oracle)
     * @param symbol Token symbol (e.g., "ETH", "USDC")
     * @param price New price in wei (18 decimals)
     */
    function updateOraclePrice(string calldata symbol, uint256 price) external onlyIndexer {
        lastOraclePrices[symbol] = price;
        lastOracleUpdate[symbol] = block.timestamp;

        emit OraclePriceUpdated(symbol, price, block.timestamp);
    }

    /**
     * @dev Check if price deviation indicates potential MEV attack
     * @param symbol Token symbol
     * @param currentPrice Current market price
     * @return isAnomaly Whether price deviation is anomalous
     */
    function checkPriceAnomaly(
        string calldata symbol,
        uint256 currentPrice
    ) external view returns (bool isAnomaly) {
        uint256 oraclePrice = lastOraclePrices[symbol];
        require(oraclePrice > 0, "No oracle price available");
        require(block.timestamp - lastOracleUpdate[symbol] < 300, "Oracle price too old");

        uint256 deviation;
        if (currentPrice > oraclePrice) {
            deviation = ((currentPrice - oraclePrice) * 10000) / oraclePrice;
        } else {
            deviation = ((oraclePrice - currentPrice) * 10000) / oraclePrice;
        }

        return deviation > priceDeviationThreshold;
    }

    // Admin functions
    function setAuthorizedIndexer(address _indexer) external onlyOwner {
        authorizedIndexer = _indexer;
    }

    function setGoldskySubgraphURL(string calldata _url) external onlyOwner {
        goldkySubgraphURL = _url;
    }

    function setPriceDeviationThreshold(uint256 _threshold) external onlyOwner {
        require(_threshold <= 1000, "Threshold cannot exceed 10%");
        priceDeviationThreshold = _threshold;
    }

    function setThreatReportReward(uint256 _reward) external onlyOwner {
        threatReportReward = _reward;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // View functions
    function getThreatData(bytes32 threatId) external view returns (ThreatData memory) {
        return threats[threatId];
    }

    function getUserProtectionStatus(address user) external view returns (
        bool protected,
        uint256 level,
        uint256 reportsCount,
        uint256 preventedCount
    ) {
        return (
            isProtected[user],
            protectionLevel[user],
            threatReports[user],
            successfulPrevented[user]
        );
    }
}

contracts/MEVProtectorAdvanced.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./MEVDetector.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title MEVProtector
 * @dev Advanced MEV protection mechanism for Aegilon Protocol
 * @notice Provides real-time MEV protection for swaps and transactions
 */
contract MEVProtectorAdvanced is ReentrancyGuard, Ownable {

    MEVDetector public detector;
    address public treasury;

    // Protection fees in basis points (100 = 1%)
    uint256 public protectionFee = 100; // 1%
    uint256 public maxProtectionFee = 500; // 5% maximum

    // Protection strategies
    enum ProtectionStrategy {
        REVERT,      // Revert transaction if MEV detected
        ADJUST,      // Adjust slippage parameters
        DELAY,       // Add delay to avoid MEV
        PRIVATE_RELAY // Route through private mempool
    }

    // Protection configuration per user
    struct ProtectionConfig {
        bool isActive;
        ProtectionStrategy strategy;
        uint256 maxSlippage; // In basis points
        uint256 gasLimit;
        bool whitelistMode; // Only allow whitelisted tokens
    }

    // Swap parameters
    struct SwapParams {
        address tokenIn;
        address tokenOut;
        uint256 amountIn;
        uint256 minAmountOut;
        bytes32 pricefeedId;
        uint256 deadline;
        address recipient;
    }

    // Protection statistics
    struct ProtectionStats {
        uint256 totalProtectedSwaps;
        uint256 threatsDetected;
        uint256 threatsBlocked;
        uint256 totalValueProtected; // In USD equivalent
        uint256 feesCollected;
    }

    // State variables
    mapping(address => ProtectionConfig) public userConfigs;
    mapping(address => ProtectionStats) public userStats;
    mapping(address => bool) public whitelistedTokens;
    mapping(bytes32 => uint256) public priceDelays; // Delay queue for price-based protection

    ProtectionStats public globalStats;

    // Events
    event ProtectionActivated(
        address indexed user,
        address indexed tokenIn,
        address indexed tokenOut,
        uint256 amountIn,
        ProtectionStrategy strategy
    );

    event ThreatMitigated(
        address indexed user,
        bytes32 indexed feedId,
        MEVDetector.ThreatType threatType,
        uint256 riskScore,
        ProtectionStrategy strategy
    );

    event SwapProtected(
        address indexed user,
        address indexed tokenIn,
        address indexed tokenOut,
        uint256 amountIn,
        uint256 amountOut,
        uint256 protectionFee
    );

    event ConfigurationUpdated(
        address indexed user,
        ProtectionStrategy strategy,
        uint256 maxSlippage
    );

    event EmergencyStop(
        address indexed user,
        string reason
    );

    constructor(address _detector, address _treasury) Ownable() {
        detector = MEVDetector(_detector);
        treasury = _treasury;
        _transferOwnership(msg.sender);

        // Initialize global stats
        globalStats = ProtectionStats({
            totalProtectedSwaps: 0,
            threatsDetected: 0,
            threatsBlocked: 0,
            totalValueProtected: 0,
            feesCollected: 0
        });
    }

    /**
     * @dev Configure protection settings for user
     * @param strategy Protection strategy to use
     * @param maxSlippage Maximum acceptable slippage in basis points
     * @param gasLimit Gas limit for protected transactions
     * @param whitelistMode Whether to enable whitelist mode
     */
    function configureProtection(
        ProtectionStrategy strategy,
        uint256 maxSlippage,
        uint256 gasLimit,
        bool whitelistMode
    ) external {
        require(maxSlippage <= 1000, "Slippage too high"); // Max 10%
        require(gasLimit >= 100000, "Gas limit too low");

        userConfigs[msg.sender] = ProtectionConfig({
            isActive: true,
            strategy: strategy,
            maxSlippage: maxSlippage,
            gasLimit: gasLimit,
            whitelistMode: whitelistMode
        });

        emit ConfigurationUpdated(msg.sender, strategy, maxSlippage);
    }

    /**
     * @dev Main protection function for swaps
     * @param params Swap parameters including tokens, amounts, and feeds
     * @return success Whether swap was successful
     * @return amountOut Amount of tokens received
     */
    function protectSwap(SwapParams calldata params) 
        external 
        payable 
        nonReentrant 
        returns (bool success, uint256 amountOut) 
    {
        require(params.deadline >= block.timestamp, "Swap expired");
        require(params.amountIn > 0, "Invalid amount");

        ProtectionConfig storage config = userConfigs[msg.sender];
        require(config.isActive, "Protection not configured");

        // Check whitelist if enabled
        if (config.whitelistMode) {
            require(whitelistedTokens[params.tokenIn], "Token not whitelisted");
            require(whitelistedTokens[params.tokenOut], "Token not whitelisted");
        }

        // Calculate expected price and current gas
        uint256 expectedPrice = _calculateExpectedPrice(params);
        uint256 currentGasPrice = tx.gasprice;

        // Run MEV detection
        (
            bool isThreat,
            MEVDetector.ThreatType threatType,
            uint256 riskScore
        ) = detector.detectMEV(params.pricefeedId, expectedPrice, currentGasPrice);

        // Update statistics
        globalStats.totalProtectedSwaps++;
        userStats[msg.sender].totalProtectedSwaps++;

        if (isThreat) {
            globalStats.threatsDetected++;
            userStats[msg.sender].threatsDetected++;

            // Apply protection strategy
            bool blocked = _applyProtectionStrategy(
                config.strategy,
                params,
                threatType,
                riskScore
            );

            if (blocked) {
                globalStats.threatsBlocked++;
                userStats[msg.sender].threatsBlocked++;

                emit ThreatMitigated(
                    msg.sender,
                    params.pricefeedId,
                    threatType,
                    riskScore,
                    config.strategy
                );

                return (false, 0); // Swap blocked
            }
        }

        // Execute protected swap
        (success, amountOut) = _executeSwap(params, config);

        if (success) {
            // Collect protection fee
            uint256 feeAmount = _collectProtectionFee(params.amountIn);

            // Update statistics
            globalStats.totalValueProtected += _estimateUSDValue(params.tokenIn, params.amountIn);
            globalStats.feesCollected += feeAmount;
            userStats[msg.sender].totalValueProtected += _estimateUSDValue(params.tokenIn, params.amountIn);
            userStats[msg.sender].feesCollected += feeAmount;

            emit SwapProtected(
                msg.sender,
                params.tokenIn,
                params.tokenOut,
                params.amountIn,
                amountOut,
                feeAmount
            );
        }

        emit ProtectionActivated(
            msg.sender,
            params.tokenIn,
            params.tokenOut,
            params.amountIn,
            config.strategy
        );

        return (success, amountOut);
    }

    /**
     * @dev Apply specific protection strategy based on threat detection
     * @param strategy Protection strategy to apply
     * @param params Swap parameters
     * @param threatType Type of MEV threat detected
     * @param riskScore Risk score from detector
     * @return blocked Whether the transaction was blocked
     */
    function _applyProtectionStrategy(
        ProtectionStrategy strategy,
        SwapParams calldata params,
        MEVDetector.ThreatType threatType,
        uint256 riskScore
    ) internal returns (bool blocked) {

        if (strategy == ProtectionStrategy.REVERT) {
            // Always block if strategy is REVERT
            return true;

        } else if (strategy == ProtectionStrategy.ADJUST) {
            // Adjust slippage based on risk score
            // Higher risk = more conservative slippage
            // This doesn't block, but modifies the swap
            return false;

        } else if (strategy == ProtectionStrategy.DELAY) {
            // Add delay based on threat type
            uint256 delayTime = _calculateDelay(threatType, riskScore);
            priceDelays[params.pricefeedId] = block.timestamp + delayTime;
            return true; // Block for now, user can retry after delay

        } else if (strategy == ProtectionStrategy.PRIVATE_RELAY) {
            // Route through private mempool (simulated)
            // In production, this would integrate with Flashbots or similar
            return false; // Allow but with different routing
        }

        return false;
    }

    /**
     * @dev Execute the actual swap with protection
     * @param params Swap parameters
     * @param config User protection configuration
     * @return success Whether swap was successful
     * @return amountOut Amount received from swap
     */
    function _executeSwap(
        SwapParams calldata params,
        ProtectionConfig storage config
    ) internal returns (bool success, uint256 amountOut) {

        // Transfer tokens from user
        IERC20(params.tokenIn).transferFrom(
            msg.sender,
            address(this),
            params.amountIn
        );

        // Simulate swap execution (in production, integrate with DEX)
        // For testnet, we'll simulate the swap with oracle prices
        uint256 currentPrice = detector.getCurrentPrice(params.pricefeedId);
        amountOut = (params.amountIn * currentPrice) / 1e18;

        // Apply slippage protection
        if (amountOut < params.minAmountOut) {
            // Refund if slippage too high
            IERC20(params.tokenIn).transfer(msg.sender, params.amountIn);
            return (false, 0);
        }

        // Check against user's max slippage
        uint256 expectedOut = (params.amountIn * currentPrice) / 1e18;
        uint256 slippage = expectedOut > amountOut ? 
            ((expectedOut - amountOut) * 10000) / expectedOut : 0;

        if (slippage > config.maxSlippage) {
            // Refund if exceeds user's slippage tolerance
            IERC20(params.tokenIn).transfer(msg.sender, params.amountIn);
            return (false, 0);
        }

        // Transfer output tokens to recipient
        // In real implementation, this would come from DEX
        // For testing, we simulate with a mock ERC20
        address recipient = params.recipient == address(0) ? msg.sender : params.recipient;

        // Simulate successful swap
        return (true, amountOut);
    }

    /**
     * @dev Calculate protection fee and transfer to treasury
     * @param amountIn Input amount for fee calculation
     * @return feeAmount Fee collected
     */
    function _collectProtectionFee(uint256 amountIn) internal returns (uint256 feeAmount) {
        feeAmount = (amountIn * protectionFee) / 10000;

        // Collect fee in ETH/XTZ
        if (msg.value >= feeAmount) {
            payable(treasury).transfer(feeAmount);
        }

        return feeAmount;
    }

    /**
     * @dev Calculate expected price for swap
     * @param params Swap parameters
     * @return expectedPrice Expected price from oracle
     */
    function _calculateExpectedPrice(SwapParams calldata params) 
        internal 
        view 
        returns (uint256 expectedPrice) 
    {
        return detector.getCurrentPrice(params.pricefeedId);
    }

    /**
     * @dev Calculate delay time based on threat type and risk score
     * @param threatType Type of MEV threat
     * @param riskScore Risk score (0-10000)
     * @return delayTime Delay in seconds
     */
    function _calculateDelay(
        MEVDetector.ThreatType threatType,
        uint256 riskScore
    ) internal pure returns (uint256 delayTime) {
        if (threatType == MEVDetector.ThreatType.SANDWICH) {
            return 30 + (riskScore / 100); // 30s base + risk-based
        } else if (threatType == MEVDetector.ThreatType.FRONTRUN) {
            return 15 + (riskScore / 200); // 15s base + risk-based
        } else if (threatType == MEVDetector.ThreatType.ARBITRAGE) {
            return 5 + (riskScore / 500); // 5s base + risk-based
        }
        return 10; // Default delay
    }

    /**
     * @dev Estimate USD value of token amount (simplified)
     * @param token Token address
     * @param amount Token amount
     * @return usdValue Estimated USD value
     */
    function _estimateUSDValue(address token, uint256 amount) 
        internal 
        pure 
        returns (uint256 usdValue) 
    {
        // Simplified USD estimation (in production, use proper price feeds)
        return amount; // 1:1 for testing
    }

    // Admin functions
    function setProtectionFee(uint256 newFee) external onlyOwner {
        require(newFee <= maxProtectionFee, "Fee too high");
        protectionFee = newFee;
    }

    function setTreasury(address newTreasury) external onlyOwner {
        require(newTreasury != address(0), "Invalid treasury");
        treasury = newTreasury;
    }

    function addWhitelistedToken(address token) external onlyOwner {
        whitelistedTokens[token] = true;
    }

    function removeWhitelistedToken(address token) external onlyOwner {
        whitelistedTokens[token] = false;
    }

    function updateDetector(address newDetector) external onlyOwner {
        require(newDetector != address(0), "Invalid detector");
        detector = MEVDetector(newDetector);
    }

    // Emergency functions
    function emergencyStop(address user, string calldata reason) external onlyOwner {
        userConfigs[user].isActive = false;
        emit EmergencyStop(user, reason);
    }

    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        if (token == address(0)) {
            payable(owner()).transfer(amount);
        } else {
            IERC20(token).transfer(owner(), amount);
        }
    }

    // View functions
    function getUserConfig(address user) external view returns (ProtectionConfig memory) {
        return userConfigs[user];
    }

    function getUserStats(address user) external view returns (ProtectionStats memory) {
        return userStats[user];
    }

    function getGlobalStats() external view returns (ProtectionStats memory) {
        return globalStats;
    }

    function isTokenWhitelisted(address token) external view returns (bool) {
        return whitelistedTokens[token];
    }

    function getPriceDelay(bytes32 feedId) external view returns (uint256) {
        return priceDelays[feedId];
    }

    // Fallback functions
    receive() external payable {}

    fallback() external payable {}
}

contracts/MockDEXRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title MockDEXRouter
 * @dev Mock DEX router for MEV attack simulation and testing
 */
contract MockDEXRouter {
    mapping(string => uint256) public prices;
    mapping(address => bool) public mevProtected;

    event SwapExecuted(address indexed user, uint256 amountIn, uint256 amountOut, string[] path);
    event PriceUpdated(string indexed pair, uint256 newPrice);
    event MEVThreatDetected(address indexed user, uint256 riskScore);

    constructor() {
        // Initialize with some default prices
        prices["ETH/USDC"] = 2000e18; // $2000 ETH
        prices["USDC/ETH"] = 1e18 / 2000; // Reciprocal
    }

    modifier noMEVThreat(address user, uint256 amount, uint256 gasPrice) {
        uint256 riskScore = calculateMEVRisk(user, amount, gasPrice);
        if (riskScore > 70) {
            emit MEVThreatDetected(user, riskScore);
            revert("MEV_THREAT_DETECTED");
        }
        _;
    }

    function setPrice(string memory pair, uint256 price) external {
        prices[pair] = price;
        emit PriceUpdated(pair, price);
    }

    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        string[] memory path,
        address to,
        uint deadline
    ) external noMEVThreat(msg.sender, amountIn, tx.gasprice) returns (uint[] memory amounts) {
        require(deadline >= block.timestamp, "EXPIRED");
        require(path.length >= 2, "INVALID_PATH");

        amounts = new uint[](path.length);
        amounts[0] = amountIn;

        // Calculate output based on mock price
        string memory pairKey = string(abi.encodePacked(path[0], "/", path[1]));
        uint256 price = prices[pairKey];
        require(price > 0, "PRICE_NOT_SET");

        amounts[1] = (amountIn * price) / 1e18;
        require(amounts[1] >= amountOutMin, "INSUFFICIENT_OUTPUT_AMOUNT");

        emit SwapExecuted(to, amountIn, amounts[1], path);
        return amounts;
    }

    function arbitrageSwap(
        address otherDex,
        uint256 amount,
        string[] memory path
    ) external returns (bool) {
        // Mock arbitrage - check if profitable
        string memory pairKey = string(abi.encodePacked(path[0], "/", path[1]));
        uint256 price1 = prices[pairKey];

        // Simulate getting price from other DEX (assume 2.5% difference)
        uint256 price2 = (price1 * 1025) / 1000;

        require(price2 > price1, "NO_ARBITRAGE_OPPORTUNITY");

        // Execute arbitrage (simplified)
        emit SwapExecuted(msg.sender, amount, (amount * price2) / 1e18, path);
        return true;
    }

    function calculateMEVRisk(
        address user,
        uint256 amount,
        uint256 gasPrice
    ) public view returns (uint256 riskScore) {
        riskScore = 0;

        // High gas price check (30 points max)
        uint256 avgGasPrice = 20e9; // 20 gwei baseline
        if (gasPrice > avgGasPrice * 2) {
            riskScore += 30;
        } else if (gasPrice > avgGasPrice * 15 / 10) {
            riskScore += 20;
        } else if (gasPrice > avgGasPrice * 12 / 10) {
            riskScore += 10;
        }

        // Large transaction check (25 points max)
        if (amount > 1000e18) { // > 1000 tokens
            riskScore += 25;
        } else if (amount > 100e18) {
            riskScore += 15;
        } else if (amount > 10e18) {
            riskScore += 5;
        }

        // Time-based pattern detection (simplified - 20 points max)
        // In real scenario, would check recent transactions
        if (block.timestamp % 100 < 20) { // Mock pattern detection
            riskScore += 20;
        }

        return riskScore;
    }

    function setMEVProtection(address user, bool protected) external {
        mevProtected[user] = protected;
    }

    function getPrice(string memory pair) external view returns (uint256) {
        return prices[pair];
    }
}

/**
 * @title MockERC20
 * @dev Simple ERC20 mock for testing
 */
contract MockERC20 {
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    string public name;
    string public symbol;
    uint8 public decimals = 18;
    uint256 public totalSupply;

    constructor(string memory _name, string memory _symbol, uint256 _supply) {
        name = _name;
        symbol = _symbol;
        totalSupply = _supply * 1e18;
        balanceOf[msg.sender] = totalSupply;
    }

    function transfer(address to, uint256 amount) external returns (bool) {
        require(balanceOf[msg.sender] >= amount, "INSUFFICIENT_BALANCE");
        balanceOf[msg.sender] -= amount;
        balanceOf[to] += amount;
        return true;
    }

    function approve(address spender, uint256 amount) external returns (bool) {
        allowance[msg.sender][spender] = amount;
        return true;
    }

    function transferFrom(address from, address to, uint256 amount) external returns (bool) {
        require(allowance[from][msg.sender] >= amount, "INSUFFICIENT_ALLOWANCE");
        require(balanceOf[from] >= amount, "INSUFFICIENT_BALANCE");

        allowance[from][msg.sender] -= amount;
        balanceOf[from] -= amount;
        balanceOf[to] += amount;
        return true;
    }

    function mint(address to, uint256 amount) external {
        balanceOf[to] += amount;
        totalSupply += amount;
    }
}

